import { css } from '../../../common/config.json';
import SpeechBubble from './SpeechBubble';

const Wrapper = styled(animated.div)`
  position: fixed;
  aspect-ratio: 1052/1092;
  width: 200px;
  height: auto;
  right: 0;
  top: 40%;
`;

const AnimateCenter = styled(animated.g)`
  transform-box: fill-box;
  transform-origin: center;
`;

const AnimateLeftHand = styled(animated.g)`
  transform-box: fill-box;
  transform-origin: 80%;
`;


