  const { shakeLimitPx, handWaveLimitDeg } = css.contact.cactus;
  const bubbleAnimationRef = useRef();

  const [hatFall, hatFallAPI] = useSpring(() => ({
    transformOrigin: "center",
    rotateZ: "0deg"
  }));

  const { hatShake } = useSpring({
    from: { hatShake: 0 },
    to: { hatShake: 1 },
    config: {
      duration: 1250,
      easing: x => 1 - Math.pow(1 - x, 3)
    },
    onRest: () => {
      hatFallAPI.start({
        transformOrigin: "right center",
        rotateZ: "-4deg",
        immediate: key => key === 'transformOrigin'
      });

      bubbleAnimationRef.current(true, 200);
    }
  });

  const { handWave } = useSpring({
    from: { handWave: 0 },
    to: { handWave: 1 },
    delay: 500,
    config: { duration: 1000 }
  });

